import axios from 'axios'
export default {
  namespaced: true,
  state () {
    return {
      list: []
    }
  },
  mutations: {
    upDateList (state, newList) {
      state.list = newList
    },
    handleCount (state, newCount) {
      const goods = state.list.find(item => item.id === newCount.id)
      goods.count = newCount.count
    }
  },
  actions: {
    async  getList (context) {
      const result = await axios.get('http://localhost:3000/cart')
      console.log(result)

      context.commit('upDateList', result.data)
    },
    async getChangeCount (context, id) {
      const result = await axios.patch(`http://localhost:3000/cart/${id}`)
      context.commit()
    }
  },
  getters: {}
}
