import axios from 'axios'
export default {
  namespaces: true,
  state () {
    return {
      list: []
    }
  },
  mutations: {
    upDateList (state, newList) {
      state.list = newList
    }
  },
  actions: {
    async  getList (context) {
      const result =await axios.get('http://localhost:3000/cart')
      console.log(result)

      context.cpmmit('upDateList', result)
    }
  },
  getters: {}
}
